package com.syntax.Reviewcalss2;

public class ArithemticOps {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char a= 'A';
		char b='B';
		
		System.out.println(a+b); //In cHR the number behhins thme
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);
		
		System.out.println("**************");
		
		
		int number=20;
		int number2=30;
		System.out.println(number/number2); 
		System.out.println(number%number2); //in %module when we do smaller 
		//number with bigger# always the result is smaller
		
		double num3=10;
		double num4=20;
		System.out.println(num3%num4); 
		
		double num5=-10;
		double num6=-20;
		System.out.println(num6%num5); // in module will negative sign
		
		num3=10.5;
		num4=5.5;
		
		System.out.println(num3%num4); 
		
		
		
		

	}

}
