package com.syntax.class01;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// representing and Storing Whole #
		byte box1 = 127; // bytes have range that can store less or =127 8bit storage
		short box2 = 1000; // store -32768 to 32767 less popular
		int box3 = 100000; // most popular data type to use for whole number
		long box4 = 100000000L; // specif long

		// represendting and storing decimal number.

		float a = 1.99f;// Float will hold small decimal
		double b = 19.9989849; // most popular data type to use for decimal number

		// represendting and storing decimal number.
		char character = '#';
		char letter = 'c';
		// representing and storing boolean values (yes or no)
		boolean hungry = true; // yes
		System.out.println(box1);
		System.out.println("box1");
	
		//System.out.println(box1);access only variable that we created
		boolean tried = false; // no

	}

}
