package com.syntax.class01;

public class PrintOrPrintln {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("This is print");
		System.out.println("This is println command");
		System.out.println("Hello");
		/*print---> just print the statement and leave cursor on same line
		* where as println----> will print the statement and will move to next line
		*/

	}

}
