package com.syntax.class03;

public class EvenorOddNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Homework#2 -- 3) Interview  Question , write a java program to check whether number is odd or even.
		
		int number=40;
		
		
		if( number% 2 ==0) { // we can find is the number is  even or odd by using %
			
			System.out.println( "The number " +  number + " is  even number");
			
			
		}else {
			
			System.out.println( "The number " +  number + " is odd number");
		} 

	}

}
