package com.syntax.class03;

public class Recap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double number7=10;
		number7++;
		System.out.println(number7);
		System.out.println(number7++); //preincremetal & post increament
		double number8=20;
		number8+=10;
		System.out.println(number8);
		number8-=40;
		System.out.println(number8);
		//number** not allowed because it does not charge anything
		//number // not to allowed because it does not charge anything
		int number6= -100;
		number6++;
		System.out.println(number6);
		
		
		
				
		

	}

}
