package com.syntax.class03;

public class TemperatureCheck {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/** Create a java program and name it Temperature.Your program should check if temperature is below 32then it should print"Water will freeze with termperature_________" , 
		otherwise "water will not freeze with temperature ". HomeWork#1 , Task#2 */
		
		int Temp=6;
		
		if (Temp<32) {
		
		   System.out.println("Water will Freeze with Temperature " + Temp + "°C"); // TO get the Celsius symbol sign Hold Alt 0176
			
		}
		
		else {
			
			 System.out.println("Water will not Freeze with Temperature " + Temp + "°C"); 
				
			
		}
		
		
		

	}

}
