package com.syntax.class07;

public class HomeWork01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Calculate sum of all even and all odd numbers from range 1 to 100
		
		int i=2;
		while( i<=100 ) {
			
			System.out.print( i+ " ");
			i+=2;
		}
		System.out.println("***************************************");
		 
		int x= 1;
		
		do {
			if(x%2!=0) {
				System.out.print(x + " ");
			}
			x+=1;
		}while(x<=100);
		
		
			
			
			
			
			
			
		
		
	}

}
