package com.syntax.class09;

public class Nest05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=5 ;i>=0;i-- ) { //outer loop control row
			
			for(int c=1; c<=4; c++) {
				System.out.print(i +" "); //control column
				
			}
			System.out.println();
		
		}
		
		System.out.println("**************************************************");
		
		for (int i=5 ;i>=0;i-- ) { //outer loop control row
			
			for(int c=1; c<=4; c++) {
				System.out.print(c +" "); //control column
				
			}
			System.out.println();
		}
	}
	}



