
package com.syntax.class10;

public class TwoDim {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Collection of 1 dimension array is called 2D  array.
		
	int [][] bigArray= new int[3][3]; //ctrl space  for auto formate  for th variables.
	
	//adding elements inside 1 array or 1 row
	bigArray[0][0] =10;
	bigArray[0][1] =20;
	bigArray[0][2] =30;
	
	//adding elements inside 2 array or 2row
	bigArray[1][0] =10;
	bigArray[1][1] =20;
	bigArray[1][2] =30;
	
	//adding elements inside 2 array or 3row
	bigArray[2][0] =10;
	bigArray[2][1] =20;
	bigArray[2][2] =30;
	
	
	
	}

}
