package com.sysntax.calss02;

public class IncrementOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Number=10;
		Number=Number+1; //result is 11
		Number=Number+Number; //result is 22
		System.out.println(Number); 
		
		System.out.println("***********");
		
		Number++; //is basically Number= Number+1 Increments the value by one //line 10(22 and it's increment by 1 so, we got the result 23
		
		
		System.out.println(Number);
		
		
		
		
				

	}

}
