public class ModOperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Number1 = 67;
		int Number2 = 5;
		System.out.println(Number1 % Number2); // if i am taking mod % the output show will be the remaining 
		System.out.println("*****************");
		Number1 = 10;
		Number2 = 47;
		System.out.println(Number1 % Number2);// if you take small  value before  the %  then large value after modl that will get smaller number as output
		System.out.println("*****************");

		System.out.println(25 % 2); 
								

	}

}
